var htmlString = document.getElementsByTagName('html')[0].innerHTML;
var parser = new DOMParser();
var links = [];
var htmlDoc = parser.parseFromString(htmlString, 'text/html');

for (i=0;i<htmlDoc.getElementsByClassName('video-container').length;i++){
	if(htmlDoc.getElementsByClassName('video-container')[i].getAttribute("data-video")){
		links.push("https:\/\/youtube.com\/watch?v="+htmlDoc.getElementsByClassName('video-container')[i].getAttribute("data-video"));
		
	};
};
// Function to download data to a file
function download(data, filename, type) {
    var file = new Blob([data], {type: type});
    if (window.navigator.msSaveOrOpenBlob) // IE10+
        window.navigator.msSaveOrOpenBlob(file, filename);
    else { // Others
        var a = document.createElement("a"),
                url = URL.createObjectURL(file);
        a.href = url;
        a.download = filename;
        document.body.appendChild(a);
        a.click();
        setTimeout(function() {
            document.body.removeChild(a);
            window.URL.revokeObjectURL(url);  
        }, 0); 
    }
}
