/*
 * caffe.css
 * For 1500chan adapted from kusaba by Anonymous
 */
 
body {
font-family: Calibri,sans-serif;
font-size: .9em;
color: #161616;
background: #ebe5b5 url(/static/cafe-0.jpg);
}

.post-hover{
  background: linear-gradient(0deg, rgba(149,177,149,.5), rgba(149,177,149,.5)), url(/static/cafe-0.jpg) !important;
  box-shadow: inset 0 1px #D2D8BB, 0 0 5px rgba(0,0,0,0.125) !important;
  }
  
div.post.reply {
border: 1px solid rgba(149,177,149,.8);
padding: 4px;
min-width: 400px;
background: rgba(149,177,149,.5);
box-shadow: inset 0 1px rgba(255,255,255,.25);
}

.omitted {
color:#666; 
font-size: .9em; 
margin-left: 20px
}

p.fileinfo {
color: #666;
font-size: .9em;
font-style: bold;
margin: 0 20px;
}

.intro span.name {
color: #ce306a;
font-weight: bold;
}

form table tr th {
font-size: .9em;
padding: 0 5px;
border: 1px solid rgba(173,87,19,.8);
background: rgba(173,87,19,.5);
box-shadow: inset 0 1px rgba(255,255,255,.4);
}

a, a:visited {
color: #000;
}
div.post.reply div.body a {
color: #000;
}
div.post.reply div.body a:hover {
color: #f00;
}

div.pages {
color: #222;
border: 1px dashed #aaa;
margin-left: 50px;
background: #0000;
}

hr {
  border-top: 0.1px solid #d0cba0;
}

h1, h3, h2 {
	font-family: Verdana,Tahoma,sans-serif;
}

h2 {
	margin: 0px;
	color: #111;
	text-shadow: 0px 1px 0px rgb(230,230,180);
	border: 1px solid #888;
	background: url(/static/cafe-4.jpg);
	padding: 6px;
}

h2 {
  color: #AF0A0F;
  font-size: 11pt;
  margin: 0;
  padding: 0;
}

div.ban h2 {
 background: url(/static/cafe-4.jpg);
 color:black;
 font-size: 12pt;
}

div.module, div.ban {
 background: #ebe5b5 url(/static/cafe-0.jpg);
 border: 1px solid #888;
 max-width: 700px;
 margin: 30px auto;
}

div.post.reply.highlighted {
 padding: 4px;
 border: 1px dashed #65ab6b;
 background-color: transparent;
 transition-duration: 0.125s;

}

div.post.reply {
		box-shadow: inset 0 1px #D2D8BB, 0 0 5px rgba(0,0,0,0.125)
}

div.boardlist {
    margin-top: 3px;
    color: rgba(33, 47, 33, 0.4);
    font-size: 10pt;
    word-spacing: 0px;
}

.desktop-style div.boardlist:not(.bottom) {
    position: fixed;
    top: 0;
    left: 0;
    right: 0;
    margin-top: 0;
    z-index: 30;
    box-shadow: 0 1px 2px rgba(0, 0, 0, .15);
    border-bottom: 1px solid;
    background-color: #c8c298c9;
}

table.modlog tr th {
color: #111;
text-shadow: 0px 1px 0px rgb(230,230,180);
border: 1px solid #888;
margin-top: 1px;
background: url(/static/cafe-4.jpg);
font-size: 100%;
padding: 8px;
}

#options_div, #alert_div {
margin-top: 50px;
background:#ebe5b5;
}

.quick-reply-btn {
  border: 3px solid #fbd182;
  border-radius:5px;
  right:15px!important;
  bottom:5px!important;
  background:#ffe9a4! important;
  box-shadow:0 1px 2px rgb(0 0 0/15%);
  font-weight:700;
  color:black;
}
